'use strict';

/***********************************
 *** START ~ Module dependencies ***
 ***********************************/

// Global dependencies
const path = require('path');

// Project dependencies
const helper = require('./helper');


/***********************************
 *** END ~ Module dependencies ***
 ***********************************/

module.exports = (settings) => {
    // Globbing microservice files
    if (settings.microservices) {
        helper.getGlobbedFiles(settings.microservices).forEach((microservice) => {
            require(path.resolve(microservice));
        });
    }
};