'use strict';

/***********************************
 *** START ~ Module dependencies ***
 ***********************************/

// Global dependencies
const _ = require('lodash');


/***********************************
 *** END ~ Module dependencies ***
 ***********************************/

let settings = null;

module.exports = (appSettings) => {
    if(!settings) {
        settings = _.extend({}, require('./settings.json'), appSettings || {});
    }

    return settings;
};